import React, { useState } from "react";

const UserInfo = [{ id, username, age }];

function App() {
  const [name, setName] = useState(UserInfo);
  const nameChangeHandler = () => {
    setName();
  };
  const onButtonHandler = (UserInput) => {
    setName((prevUserInput) => {
      return [UserInput, ...prevUserInput];
    });
  };
  return (
    <div>
      <form>
        <div>Username</div>
        <input type="text" onChange={nameChangeHandler} />
        <div>Age(Years)</div>
        <input type="number" />
      </form>
      <button type="submit" onClick={onButtonHandler}>
        Add User
      </button>
    </div>
  );
}

export default App;
